import jieba
import json
import codecs
import jieba.analyse

print("斷詞中...")
jieba.load_userdict("userdict.txt")
jieba.analyse.set_stop_words("stopword.txt")
hash = {}
for paragraph in file1:
    #print(paragraph["complain_content"])
    seg_list = jieba.analyse.extract_tags(paragraph["complain_content"])#jieba.cut(paragraph["complain_content"],cut_all=False)
    for word in seg_list:
        if word in hash:
            hash[word]+=1
        else:
            hash[word] = 1
            print(word)

json.dump(hash, codecs.open("count.json", 'w', encoding="utf-8"), indent=4, ensure_ascii=False)